import "core-js/modules/es.array.push.js";
export default {
  data() {
    return {
      title: '',
      details: ''
    };
  },
  methods: {
    handleSubmit() {
      let project = {
        title: this.title,
        details: this.details,
        complete: false
      };
      fetch('http://localhost:3000/projects', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(project)
      }).then(() => {
        this.$router.push('/');
      });
    }
  }
};
