export default {
  props: ['id'],
  data() {
    return {
      title: '',
      details: '',
      uri: 'http://localhost:3000/projects/' + this.id
    };
  },
  mounted() {
    fetch(this.uri).then(res => res.json()).then(data => {
      this.title = data.title;
      this.details = data.details;
    });
  }
};
